package com.group.libraryapp.controller.bookimport com.group.libraryapp.dto.book.request.BookLoanRequestimport com.group.libraryapp.dto.book.request.BookRequestimport com.group.libraryapp.dto.book.request.BookReturnRequestimport com.group.libraryapp.service.book.BookServiceimport org.springframework.web.bind.annotation.PostMappingimport org.springframework.web.bind.annotation.PutMappingimport org.springframework.web.bind.annotation.RequestBodyimport org.springframework.web.bind.annotation.RestController@RestControllerclass BookController(  private val bookService: BookService,) {  @PostMapping("/book")  fun saveBook(@RequestBody request: BookRequest) {    bookService.saveBook(request)  }  @PostMapping("/book/loan")  fun loanBook(@RequestBody request: BookLoanRequest) {    bookService.loanBook(request)  }  @PutMapping("/book/return")  fun returnBook(@RequestBody request: BookReturnRequest) {    bookService.returnBook(request)  }}