package com.group.libraryapp.controller.userimport com.group.libraryapp.dto.user.request.UserCreateRequestimport com.group.libraryapp.dto.user.request.UserUpdateRequestimport com.group.libraryapp.dto.user.response.UserResponseimport com.group.libraryapp.service.user.UserServiceimport org.springframework.web.bind.annotation.DeleteMappingimport org.springframework.web.bind.annotation.GetMappingimport org.springframework.web.bind.annotation.PostMappingimport org.springframework.web.bind.annotation.PutMappingimport org.springframework.web.bind.annotation.RequestBodyimport org.springframework.web.bind.annotation.RequestParamimport org.springframework.web.bind.annotation.RestController@RestControllerclass UserController(  private val userService: UserService,) {  @PostMapping("/user")  fun saveUser(@RequestBody request: UserCreateRequest) {    userService.saveUser(request)  }  @GetMapping("/user")  fun getUsers(): List<UserResponse> {    return userService.getUsers()  }  @PutMapping("/user")  fun updateUserName(@RequestBody request: UserUpdateRequest) {    userService.updateUserName(request)  }  @DeleteMapping("/user")  fun deleteUser(@RequestParam name: String) {    // String? 으로 작성하면 @RequestParam이 자동으로 false로 설정됨    userService.deleteUser(name)  }}